    public static ArrayList<Movie> getData() {
            for (String[] var : drama) {
                Movie d = new Movie();
                d.setTitle(var[0]);
                d.setOverview(var[1]);
                d.setRuntime(var[2]);
                d.setRelease(var[3]);
                d.setPoster(Integer.valueOf(var[4]));
                data.add(d);
            }
            return data;
    }
